import styled from 'styled-components';
import { darken } from "polished";

import fundo from "../../assets/images/space.jpg";


export const Wrapper = styled.div`
  height: 100vh;   
  background: #ccc;
  background-image: url(${fundo});
`;

export const Header = styled.header`
    flex: 1;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 20px;

      img {
        position: absolute;
        left: 2%;
        top: -6%;
        
      
    }
`;

export const Box = styled.div`
    width: 450px;
    height: 400px;
    background: #6C7A89;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;

    h1 {
      
      color: white;
      font-family: Helvetica;    
      
      
      ;
      
    }



`;


export const Content = styled.div`

    height: 200px;
    width: 200px;
    padding: 20px;
`;

export const Footer = styled.footer`
     display: flex;
     flex-direction: column;
    justify-content: center;
    align-items: center;

    padding: 20px;

`;

export const Button = styled.button`
  display: flex;
  margin-left: 100px;
  margin-right: 50px;
  border: none;
  cursor: pointer;
  height: 45px;
  width: 300px;
  background: red;
  border-radius: 4px;
  font-weight: bold;
  


  > span {
    color: #fff;
  }
  

  :hover {
    background: ${darken(0.04, '#8159ca')}
  }
`;
