import styled from 'styled-components';
import { darken } from "polished";

import fundo from "../../assets/images/space.jpg";


export const Wrapper = styled.div`
  height: 100vh;   
  background: #ccc;
  background-image: url(${fundo});
`;

export const Header = styled.header`
    flex: 1;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 20px;

      img {
        position: absolute;
        left: 2%;
        top: -6%;
        
      
    }
`;

export const Box = styled.div`
    flex-direction: column;
    width: 450px;
    height: 650px;
    background-color: black;
    margin-left: auto;
    margin-right: auto;
    margin-top: 3%;

   

`;

export const Text = styled.div`
font-size: 30px;
color: red;
margin-top: auto;
margin-left: auto;
margin-right: auto;
padding: 50px;

font-family: Helvetica; 


`;

export const Button = styled.button`
  display: flex;
  margin-botton: 1px;
  margin-left: 100px;
  margin-right: 50px;
  border: none;
  cursor: pointer;
  height: 45px;
  width: 300px;
  background: red;
  border-radius: 4px;
  font-weight: bold;
  
  


  > span {
    color: #fff;
  }
  

  :hover {
    background: ${darken(0.04, '#8159ca')}
  }
`;
