import styled from 'styled-components';
import { darken } from "polished";

import fundo from "../../assets/images/space.jpg";


export const Wrapper = styled.div`
  height: 100vh;   
  background: #ccc;
  background-image: url(${fundo});
`;

export const Header = styled.header`
    display: flex;
    flex: 1;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 20px;

      img {
        position: absolute;
        left: 2%;
        top: -6%;
        
      
    }
`;


export const Content = styled.div`

    height: 200px;
    width: 200px;
     display: flex;
     flex-direction: column;
    justify-content: center;
    align-items: center;

    padding: 20px;

    container {
      
    }

`;

export const Footer = styled.footer`
     display: flex;
     flex-direction: column;
    justify-content: center;
    align-items: center;

    padding: 20px;

`;

export const Button = styled.button`
  margin-left: 40px;
  border: none;
  cursor: pointer;
  height: 45px;
  width: 300px;
  background: red;
  border-radius: 4px;
  font-weight: bold;
  


  > span {
    color: #fff;
  }
  

  :hover {
    background: ${darken(0.04, '#8159ca')}
  }
`;
